class Faq extends CI_Controller {

    function __construct() {
        parent::__construct();

        $this->load->model('left_panel_model');
        $this->load->model('faq_model'); //load email template model
        $this->load->library('session');   //load session library
        if ($this->session->userdata('is_logged_in') != true) {
            $this->session->set_userdata('error_msg', 'Please log in first..');
            redirect('login_cont');
        }
    }

    //============load view page of email template================//
    function index() {
        $this->load->view('includes/header');
        $this->load->view('includes/top_header');
        $this->load->view('includes/left_panel');

        $data['result'] = $this->faq_model->select_faq_mgmt();
        $this->load->view('faq/faq_typ_mgmt1', $data);

        $this->load->view('includes/footer');
    }

    //============load view page of add_article================//
    function add_faq() {
        $this->load->view('includes/header');
        $this->load->view('includes/top_header');
        $this->load->view('includes/left_panel');
        $this->load->view('faq/add_faq_type');
        $this->load->view('includes/footer');
    }

    //============insert data into article================//	
    function insert_faq() {
        if ($this->input->post('mode_article') == 'insert_faq') {
            $data_to_store = array(
                'name' => $this->input->post('faq_typ_name'),
                'status' => $this->input->post('status')
            );
            $insrt_data = $this->faq_model->insert_faq_value('faq_types', $data_to_store);
            if ($insrt_data == '1') {
                $this->session->set_userdata('success_msg', 'FAQ type inserted successfully');
                redirect('faq');
            } else {
                $this->session->set_userdata('error_msg', 'Can not insert duplicate data.');
                redirect('faq/add_faq');
            }
        }
    }

    //=============changin the status of article============//
    function change_status_to() {
        $stat_param = array(
            'status' => $this->uri->segment(3)
        );
        $id = $this->uri->segment(4);
        $updt_status = $this->faq_model->change_status_to('faq_types', $stat_param, $id);
        if ($updt_status) {
            $this->session->set_userdata('success_msg', 'Status Updated susseccfully.');
        } else {
            $this->session->set_userdata('error_msg', 'Cannot update status.');
        }
        redirect('faq');
    }

    //=============delete the data from country============//
    function del_data() {
        $id = $this->uri->segment(3);
        $del_data = $this->faq_model->del_data('faq_types', $id);
        if ($del_data) {
            $this->session->set_userdata('success_msg', 'Data deleted susseccfully.');
        } else {
            $this->session->set_userdata('error_msg', 'Cannot delete data.');
        }
        redirect('country_cont');
    }

    //======================load edit country=====================//
    function edit_faq() {
        $id = $this->uri->segment(3);
        $data['country_arr'] = $this->faq_model->get_faq_mgmt('faq_types', $id);

        $this->load->view('includes/header');
        $this->load->view('includes/top_header');
        $this->load->view('includes/left_panel');
        $this->load->view('faq/edit_faq_type', $data);
        $this->load->view('includes/footer');
    }

    //============update data into article================//	
    function update_faq() {
        $id = $this->uri->segment(3);

        $data_to_store = array(
            'name' => $this->input->post('faq_name'),
            'status' => $this->input->post('country_stat')
        );
        $insrt_data = $this->faq_model->update_faq_value('faq_types', $data_to_store, $id);
        if ($insrt_data == '1') {
            $this->session->set_userdata('success_msg', 'FAQ updated successfully');
        } else {
            $this->session->set_userdata('error_msg', 'Can not insert duplicate data.');
        }
        redirect('faq');
    }

}

?>